import React,{Component} from 'react'


class Prices extends Component {
    constructor(props) {
        super(props);
        this.state = { currency: 'GBP' }
    }
    

    render() { 
        let list='';
        if(this.state.currency==='USD'){
            list=<li className="list-group-item">
            Bit Coin Rate for {this.props.bpi.bpi.USD.description}:<span className="badge badge-primery">{this.props.bpi.bpi.USD.code}</span>
            <strong>{this.props.bpi.bpi.USD.rate}</strong>
                    </li>

        } else if (this.state.currency==='GBP'){
        list=<li className="list-group-item">
        Bit Coin Rate for {this.props.bpi.bpi.GBP.description}:<span className="badge badge-primery">{this.props.bpi.bpi.GBP.code}</span>
        <strong>{this.props.bpi.bpi.GBP.rate}</strong>
                </li>
        }
        else if (this.state.currency==='EUR'){
         list=<li className="list-group-item">
         Bit Coin Rate for {this.props.bpi.bpi.EUR.description}:<span className="badge badge-primery">{this.props.bpi.bpi.EUR.code}</span>
         <strong>{this.props.bpi.bpi.EUR.rate}</strong>
                 </li>
        }
        return ( 
        <div>
            <ul className="list-group">
                {list}
            </ul>
            <br/>
            <select name="" id="" className="form-control"></select>
        </div> );
    }
}
 
export default Prices;
